changeSet.sql:
create sequence  if not exists token_seq start with 1 increment by 1;

create table tokens (
id bigint not null,
token_value varchar(255),
token_type varchar(15) not null,
revoked boolean not null,
expired boolean not null,
user_id bigint,
constraint pk_tokens primary key (id)
);

alter table tokens add constraint uc_tokens_token_value unique (token_value);
alter table tokens add constraint fk_tokens_on_user foreign key (user_id) references users (id);
